import './App.css';
import { BrowserRouter, Route, Switch } from "react-router-dom"
import { useState } from "react"

import NavBar from "./components/NavBar/NavBar"
import Footer from "./components/Footer/Footer"
import HomePage from "./pages/HomePage"
import LoginPage from "./pages/LoginPage"
import LogoutPage from "./pages/LogoutPage"
import SignUpPage from "./pages/SignUpPage"
import ClothesPage from "./pages/ClothesPage"
import ClosetPage from "./pages/ClosetPage"
import AboutMe from "./pages/AboutMe"
// contexts
import UserContext from "./components/UserContext/UserContext"


function App() {
  const [userInfo, setUser] = useState(null)

  function updateUser(newUserInfo) {
    setUserInfo(newUserInfo)

  }

  return (
    <div className="App">
      {/* <NavBar /> */}
      <BrowserRouter>
        <UserContext.Provider value={userInfo}>
          <Switch>
            <Route exact path="/" render={() => <NavBar />} />  
            <Route exact path="/" path='/homepage' component={HomePage} />
              {/* <Route exact path="/" render={() => <HomePage />} /> */}
              <Route exact path="/login" render={(routerProps) => <LoginPage {...routerProps} handleLogin={updateUser}/>} />
              <Route exact path="/logout" render={() => <LogoutPage handleLogout={updateUser}/>} />
              <Route exact path="/signup" render={(routerProps) => <SignUpPage {...routerProps} />} />
              <Route exact path="/clothes/:clothesId" render={(routerProps) => <ClothesPage {...routerProps}/>} />
              <Route exact path="/clothes/:clothestId/closet/:closetId" render={(routerProps) => <ClosetPage {...routerProps}/>} /> 
          </Switch>


           <Footer />
        </UserContext.Provider>
      </BrowserRouter>
    </div>


  );
}

export default App;





