import React, { useState } from 'react';


function Weather() {
  const APIKEY ="f2002f7ae07db6bce3da0c22bea3c41f"


  const[form, setForm] = useState ({
      city: "",
      country: "",
  });

    async function weatherData(e){
      e.preventDefault();
      if(form.city == ""){
        alert("add values");
      } else {
        const data = await fetch(`api.openweathermap.org/data/2.5/weather?q=${form.city},${form.country}&appid=${APIKEY}`).then(res => console.log(res.json()))
      }
    }
    const handleChange = (e) => {
      let name = e.target.name;
      let value = e.target.value;
    }
    if(name =="city"){
      setForm({...form, city: value});
    }
    if(name =="country"){
      setForm({...form, country: value});
    }
    console.log(form.city, form.country)



    return (
      <div className="weather">
      <span className="title">Weather.</span>
      <br />

      <form>
        <input 
        type="text" 
        name="city"
        placeholder="city"
        />

        <input 
        type="text" 
        name="country" 
        placeholder="country"
        />


        <button className="weatherButton" onClick={(e) => weatherData(e)}></button>
      </form>

    </div>
  );


};
  export default Weather;
