import { Link } from "react-router-dom";
import { useContext, useState, useEffect } from "react" ;
import UserContext from "../components/UserContext/UserContext";
import closetAPI from "../api/closetAPI"
import Footer from '../components/Footer/Footer';
import NavBar from "../components/NavBar/NavBar";

function HomePage(props) {
    // const userInfo = useContext(UserContext)
    // const [userInfo, setUserInfo] = useState(null)
    const [token, setToken] = useState(localStorage.getItem("token"))
    console.log(token)
    const userInfo = {
        user: {
            clothes:[
                { id:1, name: 'shoes'},
                { id:2, name: 'shirt'}
            ],

        }

    }
    useEffect(() => {
        closetAPI.fetchClosets(token)

    }, [])

    function renderContent() {
        if (!userInfo) {
            return (
                <div>
                    <h2>Welcome.</h2>
                    <Link to="/loginpage"><button>Login</button></Link>
                    &nbsp;
                    <Link to="/signuppage"><button>Register</button></Link>
                </div>
            )
        }
        let clothesElements = userInfo.user.clothes.map((clothes, index) => {
            return (
                <div>
                <Link key={index} to={`clothes/${clothes.id}`}>{clothes.name}</Link>
                </div>
            )
        })
        return (

                <div>
                <h2>You are logged in as <span className="user">{userInfo.user.username}</span></h2>
                { clothesElements }
            </div>
        )
    }
    return (
        <div className="container">
            <Footer />
            { renderContent() }
        </div>
    )
}

export default HomePage;
