import { Link } from "react-router-dom";
import { useContext, useState, useEffect } from "react" ;
import closetAPI from "../api/closetAPI"
import Footer from '../components/Footer/Footer';
import UserSummary from "../components/BasicPages/UserSummary";
import Clothes from "../components/Clothes/Clothes";
import Profile from "../components/BasicPages/Profile"

function ClosetPage(props) {
    // const userInfo = useContext(UserContext)
    const [closets, setClosets] = useState(null)
    const [token, setToken] = useState(localStorage.getItem("token"))
    const [user, setUser] = useState(localStorage.getItem("user"))
    console.log(user)

    useEffect(() => {
        const resp =closetAPI.fetchClosets(token)
        resp.then(data => {
            setClosets(data)
        })


    }, [])

    function renderContent() {
        if (!closets) {
            return (
                <div>
                    <h2>Welcome.</h2>
                    <Link to="/loginpage"><button>Login</button></Link>
                    &nbsp;
                    <Link to="/signuppage"><button>Register</button></Link>
                </div>
            )
        }
        let clothesElements = closets.map((closet, index) => {
            let items = closet.clothes.map((item) => {
                return <p>{item.clothing_name}</p>
            })
            return (
                <div>
                {/* <Link key={index} to={`clothe/${clothes.id}`}>{closet.name}</Link> */}
                <h3> {closet.name} </h3>
                <hr />
                {items}
                {items.decription}
                <hr />
                </div>
            )
        })
        return (
            <div style ={{
                boxShadow: '0 2px 6px rgba(0,0,0, 0.26)',
                width: '90%',
                maxWidth: '40rem',
                margin: '2rem auto',
                border: '1px solid #ccc',
                padding: '1rem'

            }}
            >
                {/* <div> */}
              You are logged in as <span className="user"> {user} </span>
                <UserSummary />
                { clothes.description }


            </div>
        )
    }
    return (
        <div className="container">
            <h1 className="heading">My Closet.</h1>
            <Profile img="https://www.flaticon.com/svg/vstatic/svg/826/826912.svg?token=exp=1619556652~hmac=849f29d3f96fe1aba827142027ac790a" />

            { renderContent() }
            <Clothes />
            {/* <Footer /> */}
        </div>
    )
}

export default ClosetPage;
