import './App.css';
import { BrowserRouter, Route } from "react-router-dom"
import { useState } from "react"
import { Jumbotron, Button } from 'react-bootstrap';
import Header from "./components/Header/Header"
import NavBar from "./components/NavBar/NavBar"
import HomePage from "./pages/HomePage"
import LoginPage from "./pages/LoginPage"
import LogoutPage from "./pages/LogoutPage"
import SignUpPage from "./pages/SignUpPage"
import ClothesPage from "./pages/ClothesPage"
import ClosetPage from "./pages/ClosetPage"
// contexts
import UserContext from "./components/UserContext/UserContext"


function App() {
  const [userInfo, setUserInfo] = useState(null)

  function updateUserInfo(newUserInfo) {
    setUserInfo(newUserInfo)
  }

  return (
    <div className="App">
      <NavBar />
      <Jumbotron>
                <h1>Hello, world!</h1>
                <p>
                    This is a simple hero unit, a simple jumbotron-style component for calling
                    extra attention to featured content or information.
                </p>
                <p>
                    <Button variant="primary">Learn more</Button>
                </p>
                </Jumbotron>
                <BrowserRouter>
        <UserContext.Provider value={userInfo}>
          <Header />
          <main>
            <Route exact path="/" 
                render={() => <NavBar />} />  
              <Route exact path="/" 
                render={() => <HomePage />} />
              <Route exact path="/login" 
                render={(routerProps) => <LoginPage {...routerProps} handleLogin={updateUserInfo}/>} />
              <Route exact path="/logout" 
                render={() => <LogoutPage handleLogout={updateUserInfo}/>} />
              <Route exact path="/signup" 
                render={(routerProps) => <SignUpPage {...routerProps} />} />
              <Route exact path="/clothes/:clothesId" 
                render={(routerProps) => <ClothesPage {...routerProps}/>} />
              <Route exact path="/clothes/:clothestId/closet/:closetId" 
                render={(routerProps) => <ClosetPage {...routerProps}/>} />
          </main>
        </UserContext.Provider>
      </BrowserRouter>
    </div>


  );
}

export default App;





