import { useEffect, useState, useContext } from "react";
import UserContext from "../components/UserContext/UserContext";
import Closet from "../components/Closet/Closet";
import closetAPI from "../api/closetAPI";

function ClosetPage(props) {

    const [closet, setCloset] = useState(null)


    const userInfo = useContext(UserContext)


    useEffect(() => {
        getCloset(props.match.params.closetId)
    }, [props.match.params.closetId])

    async function getCloset(closetId) {
        let token = userInfo ? userInfo.token : ""
        let data = await closetAPI.fetchCloset(closetId, token)
        setCloset(data)
    }

    const addClothes = (e) =>{
        e.preventDefault() 
        let clothingObject = {
            type: e.target.type.value

        }
    }
    return (
        <div>
            <Closet closet={closet} />
            <Button onClick={ addClothes } > add </Button>
        </div>
    )
}

export default ClosetPage;
