import closetAPI from "./src/closetAPI"
import { Jumbotron } from 'react-bootstrap'
import { Link } from "react-router-dom"


function LogIn(props) {
  async function handleLogin(e) {
    e.preventDefault()
    let credentails ={
      username: e.target.username.value,
      password: e.target.password.value
    }

    let data = await closetAPI.login(credentails)
    if(data && data.token){
      let userInfo = {
          token: data.token, 
          user: data.user
      }
      props.handleLogin(userInfo)
      props.history.push("/")
    }
}

  return (

    <Container>
        <Jumbotron>
        < h2>Account Login</h2>
              <p>
                  Welcome to Closet a personalize virtual closet that helps you keep track of your clothing items.
              </p>
              <p>
              <button onSubmit={handleLogin} className="form-button" type="submit">Login</button>
              &nbsp; &nbsp; &nbsp;&nbsp;
              <button onSubmit={handleLogin} className="form-button" type="submit">Signup</button>
              <p className="gray">New user? Click <Link to="/signup">here</Link> to register</p>
            </p>
            </Jumbotron>

  </Container>

  )
}
export default LogIn;


