import React, { useState } from 'react';
import DisplayWeather from "./DisplayWeather"

function Weather() {
  const APIKEY ="f2002f7ae07db6bce3da0c22bea3c41f"

  const[weather, setWeather] = useState([]);
  const[form, setForm] = useState ({
      city: "",
      country: "",
  });

  async function weatherData(e){
    e.preventDefault();
        if(form.city == ""){
        alert("insert values");
      } else {
        const data = await fetch(`api.openweathermap.org/data/2.5/weather?q=${form.city},${form.country}&appid=${APIKEY}`).then((res) => res.json())
        .then((data) => data );

        setWeather({ data:data });
      }
    }
  const handleChange = (e) => {
    let name = e.target.name; 
    let value = e.target.value;
  }
    if(name =="city"){
      setForm({...form, city: value});

    if(name =="country"){
      setForm({...form, country: value});
    }
  };

  return (
    <div className="weather">
      <span className="title">Weather.</span>
      <br />

      <form>
        <input 
        type="text" 
        name="city"
        placeholder="city"
        onChange={(e) => handleChange(e)}
        />

        <input 
        type="text" 
        name="country" 
        placeholder="country"
        onChange={(e) => handleChange(e)}
        />
        <button className="weatherButton" onClick={(e) => weatherData(e)}>Get Weather</button>
      </form>

      {/* {weather.data != undefined ? (
        <div>
          <DisplayWeather data = {weather.data} />
        </div>
      ): null }  */}
    </div>
  );
}
export default Weather;
