import './App.css';
import { BrowserRouter, Route, Switch } from "react-router-dom"
import { useState } from "react"

import NavBar from "./components/NavBar/NavBar"
import Footer from "./components/Footer/Footer"
import HomePage from "./pages/HomePage"
import LoginPage from "./pages/LoginPage"
import LogoutPage from "./pages/LogoutPage"
import SignUpPage from "./pages/SignUpPage"
import ClothesPage from "./pages/ClothesPage"
import ClosetPage from "./pages/ClosetPage"
import AboutMe from "./pages/AboutMe"
// contexts
import UserContext from "./components/UserContext/UserContext"


function App() {
  const [userInfo, setUserInfo] = useState(null)
  function updateUserInfo(newUserInfo) {
    setUserInfo(newUserInfo)

  }

  // const express = require("express");
  // const http = require("https");

  // const app = express();


  //   app.get("/", function (req, res) {

  //   const url = 'http://api.openweathermap.org/data/2.5/weather?&q=California&appid=f2002f7ae07db6bce3da0c22bea3c41f&units=imperial'

  //   http.get(url, function(response){
  //     console.log(response, statusCode);

  //     response.log("data", function(data){
  //       const weatherData = Json.parse(data)
  //       const temp = weatherData.main.temp
  //       const weatherDescription = weatherData[0].description
  //       res.send("The Temperature in California is " + temp + "degress F.");
  //       })
  //     })

  //   res.send("Server is up and running.")
  // })

  // app.listen(3000, function(){
  //   console.log("server is running on port 3000")
  // })




  return (
    <div className="App">
      {/* <NavBar /> */}
      <BrowserRouter>
        <UserContext.Provider value={userInfo}>
          <Switch>
            <Route exact path="/" render={() => <NavBar />} />  
            <Route exact path="/" path='/homepage' component={HomePage} />
              {/* <Route exact path="/" render={() => <HomePage />} /> */}
              <Route exact path="/login" render={(routerProps) => <LoginPage {...routerProps} handleLogin={updateUserInfo}/>} />
              <Route exact path="/logout" render={() => <LogoutPage handleLogout={updateUserInfo}/>} />
              <Route exact path="/signup" render={(routerProps) => <SignUpPage {...routerProps} />} />
              <Route exact path="/clothes/:clothesId" render={(routerProps) => <ClothesPage {...routerProps}/>} />
              <Route exact path="/clothes/:clothestId/closet/:closetId" render={(routerProps) => <ClosetPage {...routerProps}/>} /> 
          </Switch>


           <Footer />
        </UserContext.Provider>
      </BrowserRouter>
    </div>


  );
}

export default App;





