    //     let clothingObject = {
    //         clothing_name: e.target.clothing_name.value,
    //         description: e.target.description.value

    //     }
    // }
    return (
        <div>
            <Closet closet={closet} />
            {/* <Button onClick={ addClothes } > add </Button> */}
        </div>
    )
}

export default ClosetPage;
